import React from 'react';

class signUp extends React.Component {
	constructor(props) {
		super(props);
		this.state = {
		    email: '',
		    password: '',
		    name: ''
		}
	}

	onNameChange = (event) => {
		this.setState({name: event.target.value})
	}

	onEmailChange = (event) => {
		this.setState({email: event.target.value})
	}

	onPasswordChange = (event) => {
		this.setState({password: event.target.value})
	}

	// onSubmitSignIn = () => {
